import "./App.css";
import { useState } from "react";
import RecipeIngredientContainer from "./RecipeIngredientContainer/RecipeIngredientContainer";

function App() {
  const [recipe, setRecipe] = useState({
    recipe_name: "",
    flour_total: 0,
    ingredientsFlour: 1,
    ingredientsOther: 1,
    ingredient_0_name: "",
    ingredient_0_percent: 0,
    ingredient_1_name: "",
    ingredient_1_percent: 0,
    ingredient_2_name: "",
    ingredient_2_percent: 0,
    ingredient_3_name: "",
    ingredient_3_percent: 0,
    ingredient_4_name: "",
    ingredient_4_percent: 0,
    ingredient_5_name: "",
    ingredient_5_percent: 0,
    ingredient_6_name: "",
    ingredient_6_percent: 0,
    ingredient_7_name: "",
    ingredient_7_percent: 0,
    ingredient_8_name: "",
    ingredient_8_percent: 0,
    ingredient_9_name: "",
    ingredient_9_percent: 0,
  });

  const handleChange = (e) => {
    setRecipe({ ...recipe, [e.target.name]: e.target.value });
  };

  const handleAddClick = (ingredientType) => {
    let ingredient = recipe[ingredientType];
    ingredient += 1;
    setRecipe({ ...recipe, [ingredientType]: ingredient });
  };

  const renderIngredients = () => {
    console.log(recipe);
    console.log(recipe.ingredientsOther);
    let arr = new Array(recipe.ingredientsOther.length);
    console.log(arr);

    return arr.map((key, i) => {
      let textValue = recipe[`ingredient_${i}_name`];
      let percentValue = recipe[`ingredient_${i}_percent`];
      return (
        <RecipeIngredientContainer
          key={i}
          handleChange={(e) => handleChange(e)}
          handleAddClick={(ingredientType) => handleAddClick(ingredientType)}
          ingredientTextName={`ingredient_${i}_name`}
          ingredientTextValue={textValue}
          ingredientPercentName={`ingredient_${i}_percent`}
          ingredientPercentValue={percentValue}
        />
      );
    });
  };

  return (
    <main className="recipe">
      <form>
        <div className="recipe-container">
          <legend className="recipe-container__legend">Name</legend>
          <input
            className="recipe__input_heading"
            type="text"
            placeholder="Sourdough"
            value={recipe.recipe_name}
            name="recipe_name"
            onChange={(e) => handleChange(e)}
          />
        </div>
        <div className="recipe-container">
          <legend className="recipe-container__legend">Total Flour</legend>
          <input
            className="recipe__input_heading"
            type="number"
            placeholder="g"
            value={recipe.flour_total}
            name="flour_total"
            onChange={(e) => handleChange(e)}
          />
        </div>
        <hr />
        <section className="recipe-section">
          <p className="recipe-banner-text">Flour</p>
        </section>
        <section>
          <p className="recipe-banner-text">Ingredients</p>
          {renderIngredients()}
        </section>
      </form>
    </main>
  );
}

export default App;
