import "./App.css";
import { useState } from "react";
import RecipeIngredientContainer from "./RecipeIngredientContainer/RecipeIngredientContainer";

function App() {
  const [recipe, setRecipe] = useState({
    recipe_name: "",
    flour_total: 0,
    // flour_1: {"": 0},
    ingredient_1: { name: "", percent: "" },
  });

  const handleChange = (e) => {
    setRecipe({ ...recipe, [e.target.name]: e.target.value });
  };

  // const handleAddClick = () => {};

  return (
    <main className="recipe">
      <form>
        <div className="recipe-container">
          <legend className="recipe__legend">Recipe Name</legend>
          <input
            className="recipe__input_heading"
            type="text"
            placeholder="Sourdough"
            value={recipe.recipe_name}
            name="recipe_name"
            onChange={(e) => handleChange(e)}
          />
        </div>
        <div className="recipe-container">
          <legend>Total Flour</legend>
          <input
            className="recipe__input_heading"
            type="number"
            placeholder="g"
            value={recipe.flour_total}
            name="flour_total"
            onChange={(e) => handleChange(e)}
          />
        </div>
        <section className="recipe-section">
          <p className="receipt__">Flour</p>
        </section>
        <section>
          <p>Ingredients</p>
          <RecipeIngredientContainer
            placeholderName="Ingredient"
            ingredient={recipe.ingredient_1}
            handleChange={(e) => handleChange(e)}
          />
        </section>
      </form>
    </main>
  );
}

export default App;
