import React from "react";

function RecipeIngredient(props) {
  return (
    <div>
      <div className="recipe__container">
        <input
          className="recipe__input_name"
          type="text"
          name="ingredient-name-1"
          placeholder={props.placeholderName}
        />
        <input
          className="recipe__input_percent"
          type="number"
          name={props.name}
          placeholder="%"
          value={props.value}
        />
        <p className="recipe__calculated-weight">grams</p>
        <button className="recipe__button_delete">-</button>
      </div>
      <button>Add</button>
    </div>
  );
}

export default RecipeIngredient;
