import "./App.css";
import { useState } from "react";
import RecipeIngredient from "./RecipeIngredient/RecipeIngredient";

function App() {
  const [recipe, setRecipe] = useState({
    recipe_name: "",
    flour_total: 0,
  });

  const handleChange = (e) => {
    setRecipe({ ...recipe, [e.target.name]: e.target.value });
  };

  // const handleAddClick = () => {};

  return (
    <main className="recipe">
      <form>
        <div className="recipe__container">
          <legend className="recipe__legend">Recipe Name</legend>
          <input
            className="recipe__recipe-name"
            type="text"
            placeholder="Sourdough"
            value={recipe.recipe_name}
            name="recipe_name"
            onChange={() => handleChange()}
          />
        </div>
        <div className="recipe__container">
          <legend>Total Flour</legend>
          <input type="number" name="total_flour" placeholder="g" />
        </div>
        <h2>Flour</h2>
        {/* component */}
        <div className="recipe__container">
          <input
            className="recipe__input_name"
            type="text"
            name="flour-1"
            placeholder="Type of Flour"
          />
          <input
            className="recipe__input_percent"
            type="number"
            name="flour-1"
            placeholder="%"
          />
          <p className="recipe__calculated-weight">grams</p>
          <button className="recipe__button_delete">-</button>
        </div>
        <button>Add</button>

        <h2>Ingredients</h2>
        <RecipeIngredient />
      </form>
    </main>
  );
}

export default App;
