  const handleChangeFlours = (e) => {
    console.log(e.target.name);
    //   setFlours({flours.map(el => (el.id === id ? {...el, text} : el))
    // });

    // setFlours({ ...recipe, [e.target.name]: e.target.value });
  };

  const renderFlours = () => {
    return flours.map((key, i) => {
      const idProperty = Object.keys(key)[1];
      const idValue = key[idProperty];
      const ingredientTextValue = Object.keys(key)[0];
      const ingredientPercentValue = key[ingredientTextValue];

      return (
        <RecipeIngredientContainer
          key={idValue}
          id={idValue}
          ingredientCount={i}
          ingredientTextValue={ingredientTextValue}
          ingredientPercentValue={ingredientPercentValue}
          handleChange={(e) => handleChangeFlours(e)}
          // handleSubtractClick={(ingredientType) =>
          //   handleSubtractClick(ingredientType)
          // }
        />
      );
    });
  };

  // const handleAddClick = (e, ingredientType) => {
